package Assign_op;

public class arithmetic_op {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=20;
		int result=x+y;
		System.out.println(result);
		result=x-y;
		System.out.println(result);
		result=y/x;
		System.out.println(result);
		result=x*y;
		System.out.println(result);
		result=x%3;
		System.out.println(result);
	}

}
