package Assign_op;

public class assignment_op {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4;
		int var;
		var=a;
		System.out.println("var using =:"+var);
		var=+a;
		System.out.println("var using +=:"+var);
		var*=a;
		System.out.println("Var using *=:"+var);
		
	}

}
